mapper.select:

		select no, name
		  from user
  		 where email=#{e }
  		   and password=#{p }


		select no, email, name, gender
		  from user
  		 where no=#{no }

choose.when:

	update user
       set name=#{name },
           gender=#{gender }
     where no=${no }

choose.otherwise:

update user
   set name=#{name },
       password=#{password }, 
       gender=#{gender }
 where no=${no }
